import { useEffect, useState } from 'react'
import Styles from './TODO.module.css'
import { dummy } from './dummy'
import axios from 'axios';

export function TODO(props) {
    const [newTodo, setNewTodo] = useState('')
    const [todoData, setTodoData] = useState(dummy);
    const [loading, setLoading] = useState(true);
    const [editingTodo, setEditingTodo] = useState(null); // New state variable to track the todo being edited
    const [editedTitle, setEditedTitle] = useState(''); // New state variable to store the edited title

    useEffect(() => {
        const fetchTodo = async() => {
            const apiData = await getTodo()
            setTodoData(apiData);
            setLoading(false)
        }
        fetchTodo();
    }, [])

    // ... (existing code for getTodo, addTodo, deleteTodo)

    const getTodo = async() => {
        const options = {
            method: "GET",
            url: `http://localhost:8000/api/todo`,
            headers: {
                accept: "application/json",
            }
        }
        try {
            const response = await axios.request(options)
            return response.data
        } catch (err) {
            console.log(err);
            return []; // return an empty array in case of error
        }
    }

    const editTodo = (id) => {
        const todoToEdit = todoData.find(todo => todo._id === id);
        setEditingTodo(todoToEdit);
        setEditedTitle(todoToEdit.title);
    }

    const saveTodoEdit = () => {
        const options = {
            method: "PATCH",
            url: `http://localhost:8000/api/todo/${editingTodo._id}`,
            headers: {
                accept: "application/json",
            },
            data: {
                ...editingTodo,
                title: editedTitle
            }
        }
        axios
            .request(options)
            .then(function(response) {
                console.log(response.data)
                setTodoData(prevData => prevData.map(todo => todo._id === editingTodo._id ? response.data : todo))
                setEditingTodo(null);
                setEditedTitle('');
            })
            .catch((err) => {
                console.log(err)
            })
    }

    return ( <
            div className = { Styles.ancestorContainer } >
            <
            div className = { Styles.headerContainer } > { /* ... (existing code) */ } <
            /div> <
            div id = 'todoContainer'
            className = { Styles.todoContainer } > {
                loading ? ( <
                    p style = {
                        { color: 'white' }
                    } > Loading... < /p>
                ) : (
                    todoData.length > 0 ? (
                        todoData.map((entry, index) => ( <
                            div key = { entry._id }
                            className = { Styles.todo } >
                            <
                            span className = { Styles.infoContainer } >
                            <
                            input type = 'checkbox'
                            checked = { entry.done }
                            onChange = {
                                () => {
                                    updateTodo(entry._id);
                                }
                            }
                            /> { entry.title } < /
                            span > <
                            span style = {
                                { cursor: 'pointer' }
                            }
                            onClick = {
                                () => {
                                    deleteTodo(entry._id);
                                }
                            } >
                            Delete <
                            /span> <
                            span style = {
                                { cursor: 'pointer' }
                            }
                            onClick = {
                                () => {
                                    editTodo(entry._id);
                                }
                            } >
                            Edit <
                            /span> < /
                            div >
                        ))
                    ) : ( <
                        p className = { Styles.noTodoMessage } > No tasks available.Please add a new task. < /p>
                    )
                )
            } <
            /div> {
            editingTodo && ( <
                div className = { Styles.modal } >
                <
                div className = { Styles.modalContent } >
                <
                span className = { Styles.close }
                onClick = {
                    () => setEditingTodo(null)
                } > & times; < /span> <
                input type = "text"
                value = { editedTitle }
                onChange = {
                    (e) => setEditedTitle(e.target.value)
                }
                /> <
                button onClick = { saveTodoEdit } > Save < /button> < /
                div > <
                /div>
            )
        } <
        /div>
)
}
